import React, { useContext } from 'react';
import { StoreContext } from '../contexts/StoreContext';

const AddToCart = ()=> {
  const { updateProductCount } = useContext(StoreContext)
  return (
    <div className="row flex">
      <div className="col-6">
        <button className="button" onClick={()=>updateProductCount(this.props.productId, "ADD_TO_CART")}>Add to Cart</button>
      </div>
      <div className="col-6">
        <div className="row flex">
          <div className="col-4">
          <button style={{backgroundColor: "#4CAF50", padding: "10px", borderRadius: "50%"}} onClick={()=>updateProductCount(this.props.productId, "ADD")}>+</button>
          </div>
          <div className="col-4">{this.props.count}</div>
          <div className="col-4">
          <button style={{backgroundColor: "#4CAF50", padding: "10px", borderRadius: "50%"}} onClick={()=>updateProductCount(this.props.productId, "SUB")}>-</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddToCart;
