import React, { Component } from 'react';
import { StoreContext } from '../contexts/StoreContext';

class AddToCart extends Component {
  static contextType = StoreContext;

  render() {
    const {addProduct} = this.context;
    return (
      <table>
        <tbody>
          <tr>
            <th>
              {/* <button onClick={addToCart(this.props.productId)}>Add to Cart</button> */}
            </th>
            <th><button onClick={addProduct(this.props.productId)}>+</button></th>
            <th>{this.props.count}</th>
            {/* <th><button onClick={removeProduct(this.props.productId)}>-</button></th> */}
          </tr>
        </tbody>
      </table>
    );
  }
}

export default AddToCart;
