import React, { Component } from 'react';
import { StoreContext } from '../contexts/StoreContext';

class Checkout extends Component {
  static contextType = StoreContext;

  render() {
    const {cart} = this.context
    return (
      <table style={{
        position: "fixed",
        bottom: 0,
        width: "100%", backgroundColor: "pink"}} >
        <tbody>
          <tr>
            <td>
              <table>
                <tbody>
                  <tr><td><span>Qty </span><span>{cart.quantity}</span></td></tr>
                  <tr><td><span>Total </span><span>{cart.totalAmount}</span></td></tr>
                </tbody>
              </table>
            </td>
            <td><button onClick={this.context.showModal}>Check Out</button></td>
          </tr>
        </tbody>
      </table>
    );
  }
}

export default Checkout;
