import React, { Component } from 'react';
import { StoreContext } from '../contexts/StoreContext';

class Checkout extends Component {
  static contextType = StoreContext;

  render() {
    const {cart} = this.context
    return (
      <div className="row footer flex" style={{ position: "fixed", bottom: 0, width: "100%"}}>
        <div className="col-3 col-s-1" />
        <div className="col-3 col-s-5" style={{backgroundColor: "pink", fontSize: 20, fontWeight: 700}}>
          <div>
            <span>Qty </span><span>{cart.quantity}</span>
          </div>
          <div>
            <span>Total </span><span>{cart.totalAmount}</span>
          </div>
        </div>
        <div className="col-3 col-s-5" style={{backgroundColor: "pink"}}>
          <button className="button" onClick={this.context.showModal}>Check Out</button>
        </div>
        <div className="col-3 col-s-1" />
      </div>

      );
    }
  }

  export default Checkout;
