import React, { Component } from 'react';
import { StoreContext } from '../contexts/StoreContext';

class AddToCart extends Component {
  static contextType = StoreContext;

  render() {
    const { updateProductCount } = this.context;
    return (
      <div className="row flex">
        {/* <table>
          <tbody>
            <tr>
              <th>
                <button className="button" onClick={()=>updateProductCount(this.props.productId, "ADD_TO_CART")}>Add to Cart</button>
              </th>
              <th><button className="button" onClick={()=>updateProductCount(this.props.productId, "ADD")}>+</button></th>
              <th>{this.props.count}</th>
              <th><button className="button" onClick={()=>updateProductCount(this.props.productId, "SUB")}>-</button></th>
            </tr>
          </tbody>
        </table> */}
        <div className="col-6">
          <button className="button" onClick={()=>updateProductCount(this.props.productId, "ADD_TO_CART")}>Add to Cart</button>
        </div>
        <div className="col-6">
          <div className="row">
            <div className="col-4">
            <button style={{backgroundColor: "#4CAF50", padding: "15px", borderRadius: "50%"}} onClick={()=>updateProductCount(this.props.productId, "ADD")}>+</button>
            </div>
            <div className="col-4">{this.props.count}</div>
            <div className="col-4">
            <button style={{backgroundColor: "#4CAF50", padding: "15px", borderRadius: "50%"}} onClick={()=>updateProductCount(this.props.productId, "SUB")}>-</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default AddToCart;
